<script>
export default {
  data(){
    return{
      counter: 0,
      message: '',
    }
  },
  methods:{
    playSqueak(){
          this.counter++
          const url = this.getSound('squeak.mp3') 
          const squeak = new Audio(url);
          squeak.play()
          this.checkTimes()
    },
    getSound(sound){
      return require(`../assets/sounds/${sound}`)
    },
    checkTimes(){
      switch(this.counter) {
        default :
        this.message = ''
        break;
        case 2:
          this.message = 'Happy Valentiens Day BB'
          break;
        case 4:
          this.message = "I love you so much"
          break;
          case 6:
            this.message = "<3"
            break;

            case 100:
              this.message = "really?? you hit it 100 times???"
              break;
      }
    }
  }
}
</script>
